import numpy as np
import numpy.typing as npt

# %% ../nbs/01_funcs.ipynb 5
def losi(
    x: npt.NDArray[np.float64] # $x$
) -> npt.NDArray[np.float64]: # $\sigma(x)=1/(1+ e\\^{-x})$
  return 1/(1+np.exp(-x))

# %% ../nbs/01_funcs.ipynb 7
def lam(
    x: npt.NDArray[np.float64] # $x$
) -> npt.NDArray[np.float64]: # $\lambda(x)$
  return (losi(x) - 0.5)/(2*x)
