# %% ../nbs/01_funcs.ipynb 6
def losi(
    x: Float[Array, ""] # $x$
) -> Float[Array, ""]: # $\sigma(x)=1/(1+ e\\^{-x})$
  r"""$\!$*
  Logistic sigmoid 関数
  $$\sigma(x)=\frac{1}{1+e^{-x}}$$
  *$\!$"""
  return 1/(1+jnp.exp(-x))

# %% ../nbs/01_funcs.ipynb 8
def lam(
    x: Float[Array, ""] # $x$
) -> Float[Array, ""]: # $\lambda(x)$
  r"""$\!$*
  Lambda 関数
  $$\lambda(x)=\frac{1}{2x}\left[\sigma(x)-\frac{1}{2}\right]$$
  *$\!$"""
  return (losi(x) - 0.5)/(2*x)
